SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_conta_paciente_desc ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_reg_w			bigint;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	select 	count(1)
	into STRICT	qt_reg_w
	from	conta_paciente_desc_item
	where	nr_seq_desconto		= nr_sequencia_p;

	if (qt_reg_w	= 0) then
		--r.aise_application_error(-20011,'NÃ£o existem itens para ser aplicado o desconto. Existem itens sem estrutura.');
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263285);
	end if;

	update	conta_paciente_desconto
	set	dt_liberacao = clock_timestamp(),
		nm_usuario_lib = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
