CREATE OR REPLACE PROCEDURE liberar_convenio_carga_preco ( cd_convenio_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
BEGIN
if	((cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '')) then
	begin
	if (ie_opcao_p = 'M') then
		begin
		update	convenio_carga_preco
		set	ie_status = 'L',
			nm_usuario = nm_usuario_p
		where	(cd_material IS NOT NULL AND cd_material::text <> '')
		and	cd_convenio = cd_convenio_p
		and	vl_item_convenio > vl_atual;
		end;
	elsif (ie_opcao_p = 'D') then
		begin
		update	convenio_carga_preco
		set	ie_status = 'L',
			nm_usuario = nm_usuario_p
		where	(cd_material IS NOT NULL AND cd_material::text <> '')
		and	cd_convenio = cd_convenio_p
		and 	vl_item_convenio <> vl_atual;
		end;
	elsif (ie_opcao_p = 'T') then
		begin
		update	convenio_carga_preco
		set	ie_status = 'L',
			nm_usuario = nm_usuario_p
		where	(cd_material IS NOT NULL AND cd_material::text <> '')
		and	cd_convenio = cd_convenio_p;
		end;
	end if;
	end;
end if;
