SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_convenio_receb ( nr_sequencia_p bigint, ie_libera_p text, nm_usuario_p text) AS $body$
DECLARE


vl_diferenca_w		double precision;
vl_adicional_w		double precision;
ie_movto_conv_receb_w	varchar(255);
cd_estabelecimento_w	smallint;
nr_seq_conta_banco_w	bigint;
dt_recebimento_w	timestamp;
ie_soma_vl_adic_w	varchar(1) := 'N';


BEGIN

if (ie_libera_p = 'L') then

	select	(coalesce(vl_deposito,0) - (vl_recebimento - coalesce(vl_despesa_bancaria,0))) * -1,
		cd_estabelecimento,
		nr_seq_conta_banco,
		dt_recebimento
	into STRICT	vl_diferenca_w,
		cd_estabelecimento_w,
		nr_seq_conta_banco_w,
		dt_recebimento_w
	from	convenio_receb
	where	nr_sequencia	= nr_sequencia_p;

	ie_soma_vl_adic_w := obter_param_usuario(27, 212, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w, ie_soma_vl_adic_w);

	select	coalesce(max(ie_movto_conv_receb),'N')
	into STRICT	ie_movto_conv_receb_w
	from 	parametro_contas_receber
	where 	cd_estabelecimento	= cd_estabelecimento_w;

	if (ie_movto_conv_receb_w = 'L') then
		CALL consiste_movto_banco(nr_seq_conta_banco_w,dt_recebimento_w);
	end if;

/*	select	nvl(sum(vl_adicional),0) * -1  Edgar 21/09/2005 OS 23434 */

	select	coalesce(sum(CASE WHEN b.ie_banco='D' THEN  vl_adicional * -1 WHEN b.ie_banco='C' THEN  vl_adicional END ),0)
	into STRICT	vl_adicional_w
	from	transacao_financeira b,
		convenio_receb_adic a
	where	a.nr_seq_trans_financ	= b.nr_sequencia
	and	nr_seq_receb		= nr_sequencia_p;

	if (coalesce(ie_soma_vl_adic_w,'N') = 'N') then

		if (vl_adicional_w = 0) and (vl_diferenca_w <> 0) then
			--r.aise_application_error(-20011,'Os valores do recebimento estão com diferença de R$ ' || vl_diferenca_w || chr(13) ||
			--			'Verifique se não falta impostos !');
			CALL wheb_mensagem_pck.exibir_mensagem_abort(194281,'VL_DIFERENCA_W='||vl_diferenca_w);
		elsif 	((vl_diferenca_w + vl_adicional_w) <> 0) then
			/*r.aise_application_error(-20011,'Os valores não fecham.' || chr(10) ||
						'Valor de diferença do recebimento - R$ ' || vl_diferenca_w || chr(10) ||
						'Valor dos impostos - R$ ' || vl_adicional_w ||' !');*/
			CALL wheb_mensagem_pck.exibir_mensagem_abort(194283,'VL_DIFERENCA_W='||vl_diferenca_w||';'||
									'VL_ADICIONAL_W='||vl_adicional_w);
		end if;
	end if;

	update	convenio_receb
	set	nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		dt_liberacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;

else
	delete FROM convenio_receb
	where nr_sequencia = nr_sequencia_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
