SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_cotacao_oc_importada ( nr_cot_compra_p bigint, nr_ordem_compra_p bigint, ie_liberar_ordem_p text, ie_envia_conf_integracao_p text, nm_usuario_p text) AS $body$
DECLARE


dt_aprovacao_w			timestamp;
nr_seq_aprovacao_w		bigint;
nr_items_sem_aprov_w		bigint;
ie_tipo_integracao_envio_w	varchar(15);

c01 CURSOR FOR
	SELECT	distinct
		nr_seq_aprovacao
	from	Ordem_Compra_Item
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	coalesce(dt_aprovacao::text, '') = '';


BEGIN

select	ie_tipo_integracao_envio
into STRICT	ie_tipo_integracao_envio_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

/*Gera processo somente se for para somente liberar a Ordem*/

if (ie_liberar_ordem_p = 'S') then
	CALL Gerar_Aprov_Ordem_Compra(nr_ordem_compra_p, null, 'S', nm_usuario_p);
end if;

/*Se for para aprovar tambem, nao deve gerar processo de aprovação e sim liberar e aprovar direto*/

if (ie_liberar_ordem_p = 'A') then
	update	ordem_compra_item
	set	dt_aprovacao	= clock_timestamp()
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	coalesce(dt_aprovacao::text, '') = '';

	update	ordem_compra
	set	dt_aprovacao	= clock_timestamp(),
		dt_liberacao	= clock_timestamp(),
		nm_usuario_lib	= nm_usuario_p
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	coalesce(dt_aprovacao::text, '') = '';
end if;

select	dt_aprovacao
into STRICT	dt_aprovacao_w
from	ordem_compra
where	nr_ordem_compra = nr_ordem_compra_p;

if (ie_envia_conf_integracao_p = 'S') and (ie_tipo_integracao_envio_w in ('1','2')) and (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
	CALL gravar_agend_integracao(96,'nr_cot_compra=' || nr_cot_compra_p || ';nr_ordem_compra=' || nr_ordem_compra_p || ';');
	
	CALL gerar_historico_cotacao(
		nr_cot_compra_p,
		Wheb_mensagem_pck.get_Texto(303070), /*'Envio da confirmação da cotação por WebService',*/
		Wheb_mensagem_pck.get_Texto(303071), /*'A confirmação da cotação foi enviada por WebService',*/
		'H',
		nm_usuario_p);
end if;

CALL enviar_email_cotacao(9,nr_ordem_compra_p,nm_usuario_p);
CALL enviar_email_cotacao(73,nr_ordem_compra_p,nm_usuario_p);
CALL enviar_email_cotacao(11,nr_ordem_compra_p,nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE liberar_cotacao_oc_importada ( nr_cot_compra_p bigint, nr_ordem_compra_p bigint, ie_liberar_ordem_p text, ie_envia_conf_integracao_p text, nm_usuario_p text) FROM PUBLIC;

