CREATE OR REPLACE PROCEDURE liberar_curriculo ( cd_pessoa_fisica_p text, nr_seq_curriculo_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_curriculo_w	bigint;

C01 CURSOR FOR
SELECT	nr_sequencia
from	pf_curriculo
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	ie_situacao	= CASE WHEN ie_operacao_p='L' THEN 'A' WHEN ie_operacao_p='E' THEN 'I' END
and	nr_sequencia	<> nr_seq_curriculo_p;


BEGIN


update	pf_curriculo
set	dt_liberacao	= CASE WHEN ie_operacao_p='L' THEN clock_timestamp() WHEN ie_operacao_p='E' THEN null END ,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p,
	ie_situacao	= 'A'
where	nr_sequencia	= nr_seq_curriculo_p;


open C01;
loop
fetch C01 into
	nr_seq_curriculo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pf_curriculo
	set	ie_situacao = CASE WHEN ie_operacao_p='L' THEN 'I' WHEN ie_operacao_p='E' THEN 'A' END ,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia	 = nr_seq_curriculo_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
