CREATE OR REPLACE PROCEDURE liberar_desfazer_notificacao ( nm_usuario_liberacao_p text, nr_sequencia_p bigint, ie_opcao_p text) AS $body$
BEGIN
if (ie_opcao_p = 'L')	then
	update Notificacao_sinan
	set DT_LIBERACAO = clock_timestamp(), NM_USUARIO_LIBERACAO = nm_usuario_liberacao_p
	where NR_SEQUENCIA = nr_sequencia_p;

elsif (ie_opcao_p = 'D') then
	update Notificacao_sinan
	set DT_LIBERACAO  = NULL, NM_USUARIO_LIBERACAO  = NULL
	where NR_SEQUENCIA = nr_sequencia_p;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
