CREATE OR REPLACE PROCEDURE liberar_desfazer_pedido_kit ( nr_seq_pedido_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE



-- opcao = L liberação D - desfazer liberação
BEGIN

if (ie_opcao_p = 'L') then
	update	agenda_pac_pedido
	set	dt_liberacao = clock_timestamp()
	where 	nr_sequencia = nr_seq_pedido_p;
elsif (ie_opcao_p = 'D') then
	update	agenda_pac_pedido
	set	dt_liberacao  = NULL
	where 	nr_sequencia = nr_seq_pedido_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
