CREATE OR REPLACE PROCEDURE liberar_dias_rest_prescr_mat ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, qt_liberacao_p bigint, nm_usuario_p text) AS $body$
BEGIN

update	prescr_material a
set	a.qt_dias_liberado = coalesce(a.qt_dias_solicitado,0),
	a.dt_aprovacao_cih = clock_timestamp()
where	a.nr_sequencia  = nr_seq_prescr_p
and	a.nr_prescricao = nr_prescricao_p;

CALL inserir_prescr_mat_lib_cih(	'T',
				nr_prescricao_p,
				nr_seq_prescr_p,
				qt_liberacao_p,
				nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
