CREATE OR REPLACE PROCEDURE liberar_dias_solic ( nr_prescricao_p bigint, nr_seq_material_p bigint) AS $body$
BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then

UPDATE		prescr_material
SET		qt_dias_liberado = qt_dias_solicitado,
		dt_liberacao	 = clock_timestamp(),
		dt_aprovacao_cih = clock_timestamp()
WHERE	nr_prescricao		= nr_prescricao_p
AND 	nr_sequencia		= nr_seq_material_p;

	end if;
end if;

commit;

end;
$body$
