CREATE OR REPLACE PROCEDURE liberar_doc_resp_consignado ( nr_sequencia_doc_resp_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

/* ie_opcao_p:
'L' -> Liberar documento
'E' -> Estornar liberação
*/
BEGIN
if (ie_opcao_p = 'L') then
	update	sup_resp_consignado
	set	dt_liberacao = clock_timestamp(),
	nm_usuario_lib = nm_usuario_p
	where	nr_sequencia = nr_sequencia_doc_resp_p;
elsif (ie_opcao_p = 'E') then
	update	sup_resp_consignado
	set	dt_liberacao  = NULL,
		nm_usuario_lib  = NULL
	where	nr_sequencia = nr_sequencia_doc_resp_p;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
