CREATE OR REPLACE PROCEDURE liberar_escalas_saep ( nr_sequencia_p bigint, nm_tabela_p text) AS $body$
DECLARE


sql_dinamico_w	varchar(2000);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then
	begin

	sql_dinamico_w := 'update ' || nm_tabela_p || ' set dt_liberacao = sysdate where nr_sequencia = ' || nr_sequencia_p;
	CALL exec_sql_dinamico('LibEscalasSAEP','truncate table w_pf_acerto_duplic');
	CALL exec_sql_dinamico('LibEscalasSAEP',sql_dinamico_w);

	end;
end if;

commit;

end;
