CREATE OR REPLACE PROCEDURE liberar_escala_aux ( nm_tabela_p text, nm_chave_p text, qt_chave_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_sep_bv_w		varchar(10);
ds_comando_w		varchar(255);


BEGIN

select 	obter_separador_bv
into STRICT	ds_sep_bv_w
;

ds_comando_w :=	' update ' || nm_tabela_p ||
		' set dt_liberacao_aux = sysdate ' ||
		' where ' || nm_chave_p || ' = :qt_chave ';


CALL exec_sql_dinamico_bv('Liberação escalas aux PEP', ds_comando_w, 'qt_chave='|| to_char(qt_chave_p) || ds_sep_bv_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
