CREATE OR REPLACE PROCEDURE liberar_esocial_lote ( nr_seq_lote_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
L - Liberar
D - Desfazer liberação
*/
BEGIN

if (ie_opcao_p = 'L') then
	update	esocial_lote
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_lote_p;
else
	update	esocial_lote
	set	dt_liberacao	 = NULL,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_lote_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
