CREATE OR REPLACE PROCEDURE liberar_estornar_controle_lav ( nr_sequencia_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p
0 - Liberar
1 - Estornar liberação
*/
BEGIN

if (ie_opcao_p = 0) then
	update	rop_ciclo_lavagem
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario_lib	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_opcao_p = 1) then
	update	rop_ciclo_lavagem
	set	dt_liberacao	 = NULL,
		nm_usuario_lib	 = NULL
	where	nr_sequencia	= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
