CREATE OR REPLACE PROCEDURE liberar_estornar_documento ( nr_seq_doc_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p
LD - Liberar registro Documento.
ED - Estornar Documento;
*/
BEGIN
if (ie_opcao_p = 'LD') then
	update	gestao_documento
	set	dt_liberacao 	= clock_timestamp(),
		nm_usuario 	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia 	= nr_seq_doc_p;
elsif (ie_opcao_p = 'ED') then
	update	gestao_documento
	set	dt_liberacao 	 = NULL,
		nm_usuario 	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia 	= nr_seq_doc_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
