SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_evolucao_passado ( cd_estabelecimento_p bigint, qt_hora_adicional_p bigint, dt_parametro_p timestamp) AS $body$
DECLARE

 
cd_evolucao_w		bigint;
dt_evolucao_w		timestamp;
qt_controle_w		bigint := 0;

c010 CURSOR FOR 
	SELECT	/*+INDEX (a EVOPACI_I5) */ 
		distinct a.cd_evolucao, 
		a.dt_evolucao 
	from	atendimento_paciente b, 
		evolucao_paciente a 
	where	a.dt_evolucao < (dt_parametro_p - qt_hora_adicional_p / 24) 
	and	a.nr_atendimento	= b.nr_atendimento 
	and	to_char(b.cd_estabelecimento)	= to_char(cd_estabelecimento_p) 
	and	coalesce(a.dt_liberacao::text, '') = '' 
	and	a.ie_evolucao_clinica <> 'AE';


BEGIN 
 
BEGIN 
CALL exec_sql_dinamico_bv('TASY','alter trigger EVOLUCAO_PACIENTE_UPDATE disable',null); /*David - OS97958*/
OPEN C010;
LOOP 
FETCH C010 into 
	cd_evolucao_w, 
	dt_evolucao_w;
EXIT WHEN NOT FOUND; /* apply on C010 */
	BEGIN 
	update	evolucao_paciente 
	set	dt_liberacao	= dt_evolucao_w 
	where	cd_evolucao	= cd_evolucao_w;
 
	qt_controle_w	:= qt_controle_w + 1;
	if (qt_controle_w = 50000) then 
		begin 
		qt_controle_w	:= 0;
		commit;
		end;
	end if;
	END;
END LOOP;
close c010;
EXCEPTION	/*David - OS97958*/
 
when others then 
    CALL exec_sql_dinamico_bv('TASY','alter trigger EVOLUCAO_PACIENTE_UPDATE enable',null);
END;
 
commit;
CALL exec_sql_dinamico_bv('TASY','alter trigger EVOLUCAO_PACIENTE_UPDATE enable',null);
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
