CREATE OR REPLACE PROCEDURE liberar_exame_pend ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_motivo_atraso_p bigint, ds_sql_item_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_resultado_w	bigint;


BEGIN


select  coalesce(max(nr_seq_resultado),0)
into STRICT	nr_seq_resultado_w
from	exame_lab_resultado
where	nr_prescricao	= nr_prescricao_p;


update	exame_lab_result_item
set	dt_atualizacao = clock_timestamp(),
	nm_usuario     = nm_usuario_p,
	nm_usuario_liberacao =  nm_usuario_p,
	dt_liberacao = clock_timestamp()
where	nr_seq_resultado   = nr_seq_resultado_w
and	nr_seq_prescr    = nr_sequencia_p
and 	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');

update	prescr_procedimento
set	ie_status_atend = 40,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia 	= nr_sequencia_p
and  	nr_prescricao 	= nr_prescricao_p;

CALL lab_registra_motivo_atraso(nr_prescricao_p, nr_seq_motivo_atraso_p, ds_sql_item_p, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
