CREATE OR REPLACE PROCEDURE liberar_executar_procedimento ( nr_seq_agecons_p bigint, nr_atendimento_p bigint, ie_tipo_pessoa_p text, ie_prescricao_p text, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
ie_recalc_contas_prov_atend_w	varchar(1);
ie_status_agenda_w			varchar(2);
nr_prescricao_w			bigint;


BEGIN 
 
select	substr(obter_status_ageservico(nr_seq_agecons_p), 1,2) 
into STRICT	ie_status_agenda_w
;
 
select	max(nr_prescricao) 
into STRICT	nr_prescricao_w 
from	prescr_medica 
where	nr_seq_agecons = nr_seq_agecons_p;
 
if (ie_status_agenda_w = 'A') or (coalesce(nr_prescricao_w::text, '') = '') then 
	begin 
	CALL gerar_prescr_agenda_servico(nr_seq_agecons_p, nm_usuario_p);
 
	select	max(nr_prescricao) 
	into STRICT	nr_prescricao_w 
	from	prescr_medica 
	where	nr_seq_agecons = nr_seq_agecons_p;
	end;
end if;
 
if (coalesce(nr_prescricao_w,0) > 0) then 
 
	ds_erro_p := liberar_prescricao( 
		nr_prescricao_w, nr_atendimento_p, ie_tipo_pessoa_p, cd_perfil_p, nm_usuario_p, ie_prescricao_p, ds_erro_p);
 
	if (coalesce(ds_erro_p::text, '') = '') then 
		begin 
		CALL gerar_proc_pac_prescricao( 
			nr_prescricao_w, 
			0, 
			cd_perfil_p, 
			866, 
			nm_usuario_p, 
			'', 
			'', 
			null);
 
		/* Agenda de Serviços - Parâmetro 92 - Ao utilizar a opção "Liberar e executar procedimento" , recalcular as contas provisórias do atendimento vinculado */
 
		ie_recalc_contas_prov_atend_w := obter_param_usuario(866, 92, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_recalc_contas_prov_atend_w);
 
		if (ie_recalc_contas_prov_atend_w = 'S') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then 
			CALL recalcular_contas_atend(nr_atendimento_p, nm_usuario_p);
		end if;
		end;
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
