CREATE OR REPLACE PROCEDURE liberar_fenotipagem (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_doacao_w   san_result_fenotipagem.nr_seq_doacao%type;


BEGIN

update	san_result_fenotipagem
set	dt_liberacao	= clock_timestamp(),
	nm_usuario_lib	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;

commit;

select 	coalesce(max(nr_seq_doacao),
	max(nr_seq_producao))
into STRICT 	nr_seq_doacao_w
from 	san_result_fenotipagem
where	nr_sequencia	= nr_sequencia_p;

CALL San_Gerar_Codigo_ISBT.san_gravar_cod_isbt_fenot(nr_seq_doacao_w);

end;
