CREATE OR REPLACE PROCEDURE liberar_ficha_ocorrencia ( nr_ficha_ocorrencia_p bigint, nm_usuario_p text, ie_tipo_p text) AS $body$
DECLARE

-- D = Desfazer Liberação
-- L =  Liberação
BEGIN

if (nr_ficha_ocorrencia_p > 0)  then
	if (ie_tipo_p = 'L') then
		update	cih_ficha_ocorrencia
		set	dt_liberacao	=	clock_timestamp(),
		nm_usuario_lib		=	nm_usuario_p,
		nm_usuario		=	nm_usuario_p,
		dt_atualizacao		=	clock_timestamp()
		where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;
	elsif (ie_tipo_p = 'D')	then
		update	cih_ficha_ocorrencia
		set	dt_liberacao	 = NULL,
		nm_usuario_lib		 = NULL,
		nm_usuario		=	nm_usuario_p,
		dt_atualizacao		=	clock_timestamp(),
		nm_usuario_desfaz = nm_usuario_p,
		dt_desfazer_lib  = clock_timestamp()

		where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;
	end if;
commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
