CREATE OR REPLACE PROCEDURE liberar_ganho_perda_passado ( cd_estabelecimento_p bigint, qt_hora_adicional_p bigint, dt_parametro_p timestamp) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
dt_medida_w		timestamp;
qt_controle_w		bigint := 0;

c010 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.dt_medida 
	from	atendimento_paciente b, 
		atendimento_perda_ganho a 
	where	a.dt_medida < (dt_parametro_p - qt_hora_adicional_p / 24) 
	and	a.nr_atendimento	= b.nr_atendimento 
	and	to_char(b.cd_estabelecimento)	= to_char(cd_estabelecimento_p) 
	and	coalesce(a.dt_liberacao::text, '') = '';


BEGIN 
 
BEGIN 
CALL exec_sql_dinamico_bv('TASY','alter trigger ATENDIMENTO_PERDA_GANHO_UPDATE disable',null);
OPEN C010;
LOOP 
FETCH C010 into 
	nr_sequencia_w, 
	dt_medida_w;
EXIT WHEN NOT FOUND; /* apply on C010 */
	BEGIN 
	update	atendimento_perda_ganho 
	set	dt_liberacao	= dt_medida_w 
	where	nr_sequencia	= nr_sequencia_w;
 
	qt_controle_w	:= qt_controle_w + 1;
	if (qt_controle_w = 50000) then 
		begin 
		qt_controle_w	:= 0;
		commit;
		end;
	end if;
	END;
END LOOP;
close c010;
EXCEPTION 
when others then 
    CALL exec_sql_dinamico_bv('TASY','alter trigger ATENDIMENTO_PERDA_GANHO_UPDATE enable',null);
END;
 
commit;
CALL exec_sql_dinamico_bv('TASY','alter trigger ATENDIMENTO_PERDA_GANHO_UPDATE enable',null);
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
