CREATE OR REPLACE PROCEDURE liberar_ganho_perda ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_seq_tipo_w		bigint;
qt_volume_w		double precision;
nr_atendimento_w	bigint;

BEGIN
 
if (nr_sequencia_p > 0) then 
	begin 
	select	nr_seq_tipo, 
		qt_volume, 
		nr_atendimento 
	into STRICT	nr_seq_tipo_w, 
		qt_volume_w, 
		nr_atendimento_w 
	from	atendimento_perda_ganho 
	where	nr_sequencia	= nr_sequencia_p;
	exception 
		when others then 
		null;
	end;
	 
	update	atendimento_perda_ganho 
	set	dt_liberacao	=	clock_timestamp() 
	where	nr_sequencia	=	nr_sequencia_p 
	and	nm_usuario	=	nm_usuario_p 
	and	coalesce(dt_liberacao::text, '') = '';
	 
	if (coalesce(qt_volume_w,0)	> 0) then 
		CALL gerar_lancamento_automatico(nr_atendimento_w,null,381,nm_usuario_p,null,nr_seq_tipo_w,null,null,null,null);
	end if;
	 
	 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
