SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_horario_agenda_cons (nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_agenda_w	bigint;
cd_agenda_w	bigint;
i		smallint;
dt_agenda_w	timestamp;
nr_seq_turno_w      bigint;
nr_seq_turno_esp_w  bigint;
ds_obs_turno_w			varchar(80);


BEGIN

begin
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	select 	max(cd_agenda),
		max(dt_agenda),
    max(nr_seq_turno),
    max(nr_seq_turno_esp)
	into STRICT	cd_agenda_w,
		dt_agenda_w,
    nr_seq_turno_w,
    nr_seq_turno_esp_w
	from	agenda_consulta
	where	nr_sequencia		= nr_seq_agenda_p;


	/* acesso exclusivo tabela */

	select	1
	into STRICT	i
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p
	for update;

	--lock table agenda_consulta in exclusive mode;
  if (nr_seq_turno_w > 0) then
    select	max(substr(ds_observacao,1,80)) 
		into STRICT	ds_obs_turno_w 
		from	agenda_turno
		where	nr_sequencia	= nr_seq_turno_w;
  else
    select 	max(substr(ds_observacao,1,80))
		into STRICT	ds_obs_turno_w 
		from 	agenda_turno_esp
    where nr_sequencia = nr_seq_turno_esp_w;
  end if;

	select	coalesce(count(*),0)
	into STRICT	qt_agenda_w
	from	agenda_consulta
	where	nr_sequencia			= nr_seq_agenda_p
	and	coalesce(ie_status_agenda,'L')	= 'N'
	and	nm_usuario_acesso		= nm_usuario_p
	and	coalesce(cd_pessoa_fisica::text, '') = ''
	and (coalesce(nm_paciente::text, '') = ''
	or ((nm_paciente IS NOT NULL AND nm_paciente::text <> '')
	and 	nm_paciente = ds_obs_turno_w));

	if (qt_agenda_w > 0) then
		update	agenda_consulta
		set	ie_status_agenda	= 'L',
			nm_usuario		= nm_usuario_p,
			nm_usuario_acesso	 = NULL
		where	nr_sequencia		= nr_seq_agenda_p;
		commit;
	end if;
end if;
exception
	when others then
	commit;
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
