SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_impedimento_doacao ( nr_seq_doacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_param_liberar_impedimento_w	varchar(10);
ie_trali_w			varchar(1);
cd_pessoa_fisica_w		varchar(10);



BEGIN


ie_param_liberar_impedimento_w := obter_param_usuario(450, 22, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_param_liberar_impedimento_w);


update	san_doacao
set	nm_usuario_lib_imped   	= nm_usuario_p,
	dt_lib_impedimento  	= clock_timestamp(),
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_doacao_p;

if (ie_param_liberar_impedimento_w = 'L') then
	update	san_doacao
	set	ie_avaliacao_final = 'A'
	where	nr_sequencia = nr_seq_doacao_p;
end if;

if (ie_param_liberar_impedimento_w = 'T') then

	ie_trali_w := obter_se_doadora_multigesta(nr_seq_doacao_p);

	if (ie_trali_w <> 'S') then

		select 	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_w
		from	san_doacao
		where 	nr_sequencia = nr_seq_doacao_p;

		ie_trali_w := san_obter_se_doador_trali(cd_pessoa_fisica_w);
	end if;

	if (ie_trali_w = 'S') then
		update	san_doacao
		set	ie_avaliacao_final 	= 'A'
		where	nr_sequencia 		= nr_seq_doacao_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
