SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_inativar_suep ( nr_sequencia_suep_p bigint, ds_justificativa_p text, ie_acao_p text) AS $body$
BEGIN

if (nr_sequencia_suep_p IS NOT NULL AND nr_sequencia_suep_p::text <> '' AND ie_acao_p IS NOT NULL AND ie_acao_p::text <> '') then

	if (ie_acao_p = 'L') then
		update	suep
		set		dt_liberacao = clock_timestamp(),
				nm_usuario = wheb_usuario_pck.get_nm_usuario
		where	nr_sequencia = nr_sequencia_suep_p;
	elsif (ie_acao_p = 'I') then
		update	suep
		set		dt_inativacao = clock_timestamp(),
				ie_situacao = 'I',
				nm_usuario_inativacao = wheb_usuario_pck.get_nm_usuario,
				ds_justificativa = ds_justificativa_p
		where	nr_sequencia = nr_sequencia_suep_p;
	elsif (ie_acao_p = 'LI') then
		update	suep
		set		dt_liberacao = clock_timestamp(),
				dt_inativacao = clock_timestamp(),
				ie_situacao = 'I',
				nm_usuario = wheb_usuario_pck.get_nm_usuario,
				nm_usuario_inativacao = wheb_usuario_pck.get_nm_usuario,
				ds_justificativa = obter_desc_expressao(322155)
		where	nr_sequencia = nr_sequencia_suep_p;
	end if;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
