CREATE OR REPLACE PROCEDURE liberar_justif_paciente_quimio ( nr_seq_atendimento_p bigint, ie_opcao_p text, nr_seq_justif_p bigint, nm_usuario_p text) AS $body$
BEGIN
begin

if (coalesce(nr_seq_atendimento_p,0) > 0) then

	if (ie_opcao_p = 'L') then
		update  paciente_atendimento
		set     dt_liberacao = clock_timestamp(),
			nm_usuario_lib = nm_usuario_p,
			ie_exige_liberacao = 'N'
		where   nr_seq_atendimento = nr_seq_atendimento_p;
		commit;
	elsif (ie_opcao_p = 'J') then
		update  paciente_atendimento
		set     nr_seq_just_atraso = nr_seq_justif_p
		where   nr_seq_atendimento = nr_seq_atendimento_p;
		commit;
	end if;

end if;

exception
when others then
	null;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
