CREATE OR REPLACE PROCEDURE liberar_laudo_sus_aghos ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_entrada_w		timestamp;
nr_seq_interno_w	bigint;

BEGIN
 
select	max(dt_entrada) 
into STRICT	dt_entrada_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select	max(nr_seq_interno) 
into STRICT	nr_seq_interno_w 
from	sus_laudo_paciente 
where	nr_atendimento = nr_atendimento_p 
and	ie_classificacao = 1 
and	coalesce(dt_liberacao::text, '') = '' 
and	dt_emissao < dt_entrada_w 
and	coalesce(dt_cancelamento::text, '') = '';
 
if (nr_seq_interno_w IS NOT NULL AND nr_seq_interno_w::text <> '') then 
	CALL sus_liberar_laudo(nr_seq_interno_w, 'L', nm_usuario_p);
end if;
 
commit;
 
end;
