SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_licitacao ( nr_seq_licitacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_itens_w	integer;


BEGIN

select	count(*)
into STRICT	qt_itens_w
from	reg_lic_item
where	nr_seq_licitacao	= nr_seq_licitacao_p;
if (qt_itens_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266167);
	--'É necessário existir itens na licitação');
end if;

update	reg_licitacao
set	dt_liberacao	= clock_timestamp(),
	nm_usuario_lib	= nm_usuario_p
where	nr_sequencia	= nr_seq_licitacao_p;

insert into reg_lic_historico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	ie_tipo_historico,
	ds_observacao,
	nr_seq_licitacao)
values (	nextval('reg_lic_historico_seq'),
	clock_timestamp(),
	nm_usuario_p,
	'L',
	WHEB_MENSAGEM_PCK.get_texto(310352), --'Liberação da licitação.',
	nr_seq_licitacao_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
