CREATE OR REPLACE PROCEDURE liberar_lista_parcela_neg_cr ( ds_lista_parcela_p text, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE

					 
nr_seq_parcela_w	bigint;

c01 CURSOR FOR 
SELECT	a.nr_sequencia 
from	negociacao_cr_parcela a 
where	' ' || ds_lista_parcela_p || ' ' like '% ' || a.nr_sequencia|| ' %' 
and	(ds_lista_parcela_p IS NOT NULL AND ds_lista_parcela_p::text <> '');
					
 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_parcela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL liberar_parcela_neg_cr(nr_seq_parcela_w,nm_usuario_p,null,ie_acao_p,null);
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
