CREATE OR REPLACE PROCEDURE liberar_lotes_agrupados ( nr_sequencia_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


qt_status_w		integer;

/*  L - liberar lotes
      E - Estonar lotes
*/
BEGIN

if (ie_opcao_p = 'L') then
	update	ap_lote_agrup
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;
end if;
if (ie_opcao_p = 'E') then
	update	ap_lote_agrup
	set	dt_liberacao	 = NULL
	where	nr_sequencia	= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
