SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_lote_audit_hist (nr_seq_lote_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
 ie_acao_p
	'L' - Liberar
	'D' - Desfazer liberação

*/
cont_w		bigint := 0;
nr_seq_retorno_w	bigint;


BEGIN

if (ie_acao_p = 'L') then
	select	a.nr_seq_retorno
	into STRICT	nr_seq_retorno_w
	from	lote_auditoria a,
		lote_audit_hist b
	where	b.nr_sequencia		= nr_seq_lote_p
	and	b.nr_seq_lote_audit	= a.nr_sequencia;

	select	count(*)
	into STRICT	cont_w
	from	convenio_retorno_item b,
		lote_audit_hist_guia a
	where	a.nr_seq_lote_hist	= nr_seq_lote_p
	and	a.nr_interno_conta	= b.nr_interno_conta
	and	a.cd_autorizacao	= b.cd_autorizacao
	and	b.nr_seq_retorno	= nr_seq_retorno_w
	and	coalesce(b.nr_titulo::text, '') = '';

	if (cont_w > 0) then
		-- Existem guias sem título!
		CALL wheb_mensagem_pck.exibir_mensagem_abort(267086);
	end if;

	update	lote_audit_hist
	set	dt_liberacao	 = clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		ie_status		= 'L'
	where	nr_sequencia	= nr_seq_lote_p;

elsif (ie_acao_p = 'D') then
	update	lote_audit_hist
	set	dt_liberacao	  = NULL,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		ie_status		= 'A'
	where	nr_sequencia	= nr_seq_lote_p;
end if;

CALL atualizar_valor_lote_recurso(nr_seq_lote_p, ie_acao_p, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
