CREATE OR REPLACE PROCEDURE liberar_lote_producao ( nr_lote_producao_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_lote_producao_p > 0) then
	update	lote_producao
	set	dt_confirmacao		=	clock_timestamp(),
		nm_usuario_confirmacao	=	nm_usuario_p
	where	nr_lote_producao	=	nr_lote_producao_p;

	CALL gerar_lote_producao_individual(nr_lote_producao_p, nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
