CREATE OR REPLACE PROCEDURE liberar_lote_protocolo ( nr_seq_lote_protocolo_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


/*

'L' - Liberar
'D' - Desfazer Liberação

*/
BEGIN

if (ie_acao_p = 'L') then

	update	lote_protocolo
	set 	dt_liberacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where 	nr_sequencia = nr_seq_lote_protocolo_p;

elsif (ie_acao_p = 'D') then

	update	lote_protocolo
	set 	dt_liberacao  = NULL,
		nm_usuario = nm_usuario_p
	where 	nr_sequencia = nr_seq_lote_protocolo_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
