CREATE OR REPLACE PROCEDURE liberar_medicamento_uso ( nr_atendimento_p bigint, cd_pessoa_fisica_p bigint, nm_usuario_p text, dt_liberacao timestamp, nr_seq_medic_uso_p bigint default 0) AS $body$
DECLARE

					 
nr_seq_evento_w			bigint;
nr_sequencia_w			bigint;
qt_idade_w				bigint;
cd_estabelecimento_w	smallint;		
cd_material_w			bigint;

C01 CURSOR FOR 
	SELECT		nr_seq_evento 
	from		regra_envio_sms 
	where		((cd_estabelecimento_w = 0) or (cd_estabelecimento	= cd_estabelecimento_w)) 
	and			ie_evento_disp = 'LMC' 
	and			qt_idade_w between coalesce(qt_idade_min,0)	and coalesce(qt_idade_max,9999) 
	and			coalesce(ie_situacao,'A') = 'A';
	
C02 CURSOR FOR 
	SELECT		nr_seq_evento 
	from		regra_envio_sms 
	where		((cd_estabelecimento_w = 0) or (cd_estabelecimento	= cd_estabelecimento_w)) 
	and			ie_evento_disp = 'LHSP' 
	and			qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,9999) 
	and			coalesce(ie_situacao,'A') = 'A' 
	and			obter_se_mat_rec_alerta(cd_material_w,nr_sequencia) = 'S';
						

BEGIN  
	cd_estabelecimento_w := obter_estabelecimento_ativo;
  qt_idade_w	:= coalesce(obter_idade_pf(cd_pessoa_fisica_p,clock_timestamp(),'A'),0);
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL gerar_evento_paciente_trigger(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_p,null,nm_usuario_p,null,dt_liberacao,NULL);
		end;
	end loop;
	close C01;
 
	 
	if (nr_seq_medic_uso_p > 0) then 
	 
		select	cd_material 
		into STRICT	cd_material_w 
		from	paciente_medic_uso 
		where	nr_sequencia = nr_seq_medic_uso_p;
	 
		open C02;
		loop 
		fetch C02 into 
			nr_seq_evento_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin 
			CALL gerar_evento_paciente_trigger(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_p,null,nm_usuario_p,null,dt_liberacao,NULL);
			end;
		end loop;
		close C02;
	end if;
	 
end;
$body$
