CREATE OR REPLACE PROCEDURE liberar_med_protocolo_receita ( nr_sequencia_p MED_PROTOCOLO_RECEITA.nr_sequencia%type, ie_opcao_p text) AS $body$
DECLARE


/*

ie_opcao_p
L = Liberar
D = Desfazer liberacao

*/
BEGIN

if (ie_opcao_p = 'L') then

update MED_PROTOCOLO_RECEITA
set DT_LIBERACAO = clock_timestamp(),
  DT_ATUALIZACAO = clock_timestamp(),
  NM_USUARIO = wheb_usuario_pck.get_nm_usuario
where nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'D') then

update MED_PROTOCOLO_RECEITA
set DT_LIBERACAO  = NULL,
  DT_ATUALIZACAO = clock_timestamp(),
  NM_USUARIO = wheb_usuario_pck.get_nm_usuario
where nr_sequencia = nr_sequencia_p;

end if;
CALL GERAR_VERSAO_ALTERACAO('MED_PROTOCOLO_RECEITA', ie_opcao_p, nr_sequencia_p, wheb_usuario_pck.get_nm_usuario);
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
