CREATE OR REPLACE PROCEDURE liberar_nota_credito ( nr_seq_nf_credito_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_sem_definicao_w	bigint;


BEGIN

if (nr_seq_nf_credito_p IS NOT NULL AND nr_seq_nf_credito_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	count(1)
	into STRICT	qt_sem_definicao_w
	from	nf_credito_item
	where	nr_seq_nf_credito = nr_seq_nf_credito_p
	and (coalesce(pr_aplicacao,0) <> 0 or coalesce(vl_aplicacao,0) <> 0 or coalesce(qt_aplicacao,0) <> 0);
	
	if (qt_sem_definicao_w = 0) then
		--Para liberar a nota de cr√©dito, deve alterar o valor de pelo menos um item.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(306941);
	end if;
	
	CALL gerar_nota_fiscal_credito(nr_seq_nf_credito_p,cd_estabelecimento_p,nm_usuario_p);

	update	nf_credito
	set	dt_liberacao = clock_timestamp(),
		nm_usuario_lib = nm_usuario_p
	where	nr_sequencia = nr_seq_nf_credito_p;

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
