CREATE OR REPLACE PROCEDURE liberar_pac_coleta ( nr_seq_doacao_p bigint, nm_usuario_p text) AS $body$
BEGIN
 
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then	 
		 
	update	san_doacao 
	set	ie_status 		= 1, 
		ie_avaliacao_final 	= 'A', 
		dt_triagem_fisica 	= clock_timestamp(), 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where 	nr_sequencia 	= nr_seq_doacao_p;
	 
	--SAN EVENTO- LIBERAR PARA A COLETA 
	CALL executa_evento_hemoterapia( nm_usuario_p , nr_seq_doacao_p , 2);
		 
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
