SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_perfil_usuario ( cd_perfil_p bigint, nm_usuario_lib_p text, nm_usuario_p text) AS $body$
DECLARE


ie_estrategico_w		varchar(1);
ie_permite_lib_perfil_est_w	varchar(1);


BEGIN

select	coalesce(max(ie_estrategico),'N')
into STRICT	ie_estrategico_w
from	perfil
where	cd_perfil	= cd_perfil_p;



if (ie_estrategico_w = 'S') then
	begin
	ie_permite_lib_perfil_est_w	:= coalesce(substr(obter_valor_param_usuario(6001,137,obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_perfil),1,1),'N');

	if (ie_permite_lib_perfil_est_w = 'N') then
		/*Usuário sem permissão para liberar perfil estratégico!*/

		CALL wheb_mensagem_pck.exibir_mensagem_abort(207168);
	end if;
	end;
end if;

if (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') and (nm_usuario_lib_p IS NOT NULL AND nm_usuario_lib_p::text <> '') then
	begin
	insert into usuario_perfil(
		nm_usuario,
		cd_perfil,
		dt_atualizacao,
		nm_usuario_atual,
		ds_observacao,
		nr_seq_apres,
		dt_liberacao)
	values ( nm_usuario_lib_p,
		cd_perfil_p,
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		clock_timestamp());
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
