CREATE OR REPLACE PROCEDURE liberar_prescricao_rep_4 ( nr_prescricao_p bigint, nr_atendimento_p bigint, ie_tipo_pessoa_p text, ie_prescricao_p text, ie_gerar_cirurgia_p text, ie_gerar_evol_alt_p text, cd_perfil_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text, cd_evolucao_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_erro_w	varchar(2000);
cd_evolucao_w	bigint;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
 
	ds_erro_w := liberar_prescricao( 
		nr_prescricao_p, nr_atendimento_p, ie_tipo_pessoa_p, cd_perfil_p, nm_usuario_p, ie_prescricao_p, ds_erro_w);
 
	if (coalesce(ds_erro_w::text, '') = '') or (ds_erro_w = '') then 
		begin 
		if (ie_gerar_cirurgia_p = 'S') then 
			begin 
			CALL gerar_mat_esp_prescr_cirurgia( 
				nr_prescricao_p, 
				nr_atendimento_p, 
				cd_estabelecimento_p, 
				nm_usuario_p);
			end;
		end if;
 
		if (ie_gerar_evol_alt_p = 'S') and (coalesce(ds_erro_w::text, '') = '') then 
			begin 
			SELECT * FROM gerar_evolucao_alteracao_item( 
				nr_prescricao_p, nm_usuario_p, cd_estabelecimento_p, null, cd_evolucao_w, ds_erro_w) INTO STRICT cd_evolucao_w, ds_erro_w;
			end;
		end if;
		/* 
		consiste_aferese_terapeutica(nr_prescricao_p, 
					cd_estabelecimento_p, 
					nm_usuario_p); 
		*/
 
		end;
	end if;
	end;
end if;
commit;
ds_erro_p	:= ds_erro_w;
cd_evolucao_p	:= cd_evolucao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
