SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_prescr_conferencia ( nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lib_conf_w	bigint;


BEGIN
if (coalesce(nr_prescricao_p,0) > 0) then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_lib_conf_w
	from	prescr_lib_conferencia
	where	nr_prescricao	= nr_prescricao_p;

	if (nr_seq_lib_conf_w = 0) then
		insert into prescr_lib_conferencia(	nr_sequencia,
							nr_prescricao,
							nm_usuario_nrec,
							nm_usuario_lib_conferencia,
							nm_usuario_conferencia,
							nm_usuario,
							dt_lib_conferencia,
							dt_inicio_conferencia,
							dt_fim_conferencia,
							dt_atualizacao_nrec,
							dt_atualizacao) values (	nextval('prescr_lib_conferencia_seq'),
							nr_prescricao_p,
							nm_usuario_p,
							nm_usuario_p,
							null,
							nm_usuario_p,
							clock_timestamp(),
							null,
							null,
							clock_timestamp(),
							clock_timestamp());
	else
		update	prescr_lib_conferencia
		set		nm_usuario_lib_conferencia	= nm_usuario_p,
				nm_usuario					= nm_usuario_p,
				dt_lib_conferencia			= clock_timestamp(),
				dt_atualizacao				= clock_timestamp()
		where	nr_sequencia				= nr_seq_lib_conf_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
