SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_prescr_parcial_farm ( nr_prescricao_p bigint, nm_usuario_p text ) AS $body$
DECLARE

 
ie_gerar_kit_w		varchar(1);
cd_estabelecimento_w	smallint;
nr_sequencia_w		bigint;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	prescr_material a 
	where	a.nr_prescricao		=	nr_prescricao_p 
	and	coalesce(a.nr_seq_inconsistencia::text, '') = '' 
	and	not exists (SELECT	1 
				from	prescr_material_incon_farm y 
				where	y.nr_prescricao 	= a.nr_prescricao 
				and	y.nr_seq_material 	= a.nr_sequencia 
				and	coalesce(y.ie_situacao,'A')	= 'A') 
	and	coalesce(a.dt_suspensao::text, '') = '';


BEGIN 
 
if (nr_prescricao_p > 0) then 
	begin 
 
	update	prescr_material a 
	set	a.dt_lib_farmacia	= clock_timestamp(), 
		a.dt_lib_material = clock_timestamp() 
	where	a.nr_prescricao	= nr_prescricao_p 
	and	coalesce(a.dt_lib_farmacia::text, '') = '' 
	and	coalesce(a.nr_seq_inconsistencia::text, '') = '' 
	and	not exists (SELECT	1 
				from	prescr_material_incon_farm y 
				where	y.nr_prescricao 	= a.nr_prescricao 
				and	y.nr_seq_material 	= a.nr_sequencia 
				and	coalesce(y.ie_situacao,'A')	= 'A') 
	and	coalesce(a.dt_suspensao::text, '') = '';
 
		update	prescr_solucao a 
	set	a.dt_lib_farmacia	= clock_timestamp(), 
		a.dt_lib_material = clock_timestamp() 
	where	a.nr_prescricao	= nr_prescricao_p 
	and	coalesce(a.dt_lib_farmacia::text, '') = '' 
	and	coalesce(a.nr_seq_inconsistencia::text, '') = '' 
	and	not exists (SELECT	1 
				from	prescr_material_incon_farm y 
				where	y.nr_prescricao 	= a.nr_prescricao 
				and	y.nr_seq_solucao 	= a.nr_seq_solucao 
				and	coalesce(y.ie_situacao,'A')	= 'A') 
	and	coalesce(a.dt_suspensao::text, '') = '';
 
	commit;
 
	open C01;
	loop 
	fetch C01 into 
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		if (ie_gerar_kit_w = 'S') then 
			CALL Gerar_Kit_Medic_Prescricao(cd_estabelecimento_w,nr_prescricao_p,nr_sequencia_w, nm_usuario_p);
		end if;
 
		CALL Gerar_prescr_mat_hor(nr_prescricao_p, nr_sequencia_w, Obter_perfil_ativo,nm_usuario_p,'N','N', null);
		end;
	end loop;
	close C01;
 
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
