CREATE OR REPLACE PROCEDURE liberar_projeto_recurso ( nr_sequencia_p projeto_recurso.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
CALL gerar_processo_aprov_proj_rec(	nr_seq_proj_rec_p => nr_sequencia_p,
				nm_usuario_p => nm_usuario_p,
				ie_commit_p => 'N');

update	projeto_recurso
set	nm_usuario_lib	= nm_usuario_p,
	dt_liberacao	= clock_timestamp(),
	dt_aprovacao	= CASE WHEN nr_seq_aprovacao = NULL THEN clock_timestamp()  ELSE dt_aprovacao END ,
	nm_usuario_aprov = CASE WHEN nr_seq_aprovacao = NULL THEN nm_usuario_p  ELSE nm_usuario_aprov END
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
