SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_protese_manutencao ( nm_usuario_p text, nr_seq_manut_p bigint, nr_seq_equip_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE




nr_ordem_servico_w	bigint;

ds_texto_w		varchar(255);



dt_inicio_desejado_w	timestamp;

ds_motivo_w		varchar(100);

ds_justificativa_w	varchar(4000);




BEGIN

ds_texto_w := '';



if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_manut_p IS NOT NULL AND nr_seq_manut_p::text <> '') then



	select	max(a.dt_envio),

		substr(max(pb_obter_desc_motivo_manut(a.nr_seq_motivo)),1,100),

		substr(max(a.ds_justificativa),1,4000)

	into STRICT	dt_inicio_desejado_w,

		ds_motivo_w,

		ds_justificativa_w

	from	rp_equip_manutencao a

	where	a.nr_sequencia = nr_seq_manut_p;



	if (coalesce(dt_inicio_desejado_w::text, '') = '') or (coalesce(ds_motivo_w::text, '') = '') or (coalesce(ds_justificativa_w::text, '') = '') then

		CALL wheb_mensagem_pck.exibir_mensagem_abort(331062);

	end if;



	update	rp_equip_manutencao

	set	dt_liberacao 	= clock_timestamp(),

		nm_usuario_lib	= nm_usuario_p

	where	nr_sequencia 	= nr_seq_manut_p;



	nr_ordem_servico_w := gerar_os_protese_manutencao(	nr_seq_manut_p, nm_usuario_p, nr_ordem_servico_w);



	ds_texto_w := substr(obter_texto_dic_objeto(279811, wheb_usuario_pck.get_nr_seq_idioma, 'ORDEM_SERVICO=' || nr_ordem_servico_w),1,255);



	CALL man_log_equip_hist_evento(	nr_seq_equip_p,

					ds_texto_w,

					'M',

					nm_usuario_p);



	commit;

end if;



ds_mensagem_p := ds_texto_w;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE liberar_protese_manutencao ( nm_usuario_p text, nr_seq_manut_p bigint, nr_seq_equip_p bigint, ds_mensagem_p INOUT text) FROM PUBLIC;

