CREATE OR REPLACE PROCEDURE liberar_receita_pac ( nr_sequencia_p bigint) AS $body$
DECLARE

 
cd_estabelecimento_w	bigint;
nr_atendimento_w	bigint;
nr_seq_evento_w		bigint;
cd_pessoa_fisica_w	varchar(10);
qt_idade_w			bigint;
C01 CURSOR FOR
	SELECT	nr_seq_evento 
	from	regra_envio_sms 
	where	cd_estabelecimento	= cd_estabelecimento_w 
	and	ie_evento_disp = 'RML' 
	and	qt_idade_w between coalesce(qt_idade_min,0)	and coalesce(qt_idade_max,9999)	 
	and	coalesce(ie_situacao,'A') = 'A';


BEGIN 
cd_estabelecimento_w		:= wheb_usuario_pck.get_cd_estabelecimento;
 
select	max(nr_atendimento_hosp), 
	max(cd_pessoa_fisica) 
into STRICT	nr_atendimento_w, 
	cd_pessoa_fisica_w 
from	med_receita 
where	nr_sequencia = nr_sequencia_p;
 
update	med_receita 
set	dt_liberacao = clock_timestamp() 
where	nr_sequencia = nr_sequencia_p;
 
qt_idade_w	:= coalesce(obter_idade_pf(cd_pessoa_fisica_w,clock_timestamp(),'A'),0);
 
open C01;
loop 
fetch C01 into 
	nr_seq_evento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') and (obter_tipo_atendimento(nr_atendimento_w)	= 1) then 
		CALL gerar_evento_paciente(nr_seq_evento_w,nr_atendimento_w,cd_pessoa_fisica_w,null,wheb_usuario_pck.get_nm_usuario,null);
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
