CREATE OR REPLACE PROCEDURE liberar_registro_kit ( nr_sequencia_p bigint, nm_usuario_lib_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


ds_motivo_exclusao_w	varchar(255) := '';
ds_erro_w		varchar(255) := '';
qt_existe_w		bigint;


BEGIN

if (obter_funcao_ativa = 143) then
		ds_motivo_exclusao_w := obter_param_usuario(143, 376, obter_perfil_ativo, nm_usuario_p, coalesce(wheb_usuario_pck.get_cd_estabelecimento,1), ds_motivo_exclusao_w);
else
		ds_motivo_exclusao_w := obter_param_usuario(50, 101, obter_perfil_ativo, nm_usuario_p, coalesce(wheb_usuario_pck.get_cd_estabelecimento,1), ds_motivo_exclusao_w);
end if;

if (ds_motivo_exclusao_w IS NOT NULL AND ds_motivo_exclusao_w::text <> '') then

	select	count(*)
	into STRICT	qt_existe_w
	from	kit_estoque_comp b,
		kit_estoque a
	where	a.nr_sequencia = b.nr_seq_kit_estoque
	and	a.nr_seq_reg_kit = nr_sequencia_p
	and	obter_se_contido(b.nr_seq_motivo_exclusao, ds_motivo_exclusao_w) = 'S'
	and	obter_saldo_disp_estoque(coalesce(wheb_usuario_pck.get_cd_estabelecimento,1), b.cd_material, a.cd_local_estoque, clock_timestamp(), null) > 0;

	if (qt_existe_w > 0) then
		ds_erro_w := substr(obter_texto_tasy(286994, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end if;
end if;

if (coalesce(ds_erro_w::text, '') = '') then

	update	kit_estoque_reg
	set	nm_usuario_lib = nm_usuario_lib_p,
		dt_liberacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

end if;

ds_erro_p := ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
