CREATE OR REPLACE PROCEDURE liberar_regra_acao_pag_duplic ( nr_seq_regra_p bigint, nm_usuario_p text, ie_acao_p text) AS $body$
DECLARE


/*
ie_acao_p
'L' - Liberar
'E' - Encerrar vigÃªncia
*/
nr_seq_regra_w		bigint;
cd_estabelecimento_w	regra_acao_pag_duplic.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	regra_acao_pag_duplic
	where	nr_sequencia <> nr_seq_regra_p
	and	coalesce(dt_fim_vigencia::text, '') = ''
	and	coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_w,0))	= coalesce(cd_estabelecimento_w,0);


BEGIN

if (ie_acao_p = 'L') then
	update	regra_acao_pag_duplic
	set	dt_liberacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_regra_p;

	select	max(a.cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	regra_acao_pag_duplic a
	where	a.nr_sequencia	= nr_seq_regra_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		update	regra_acao_pag_duplic
		set	dt_fim_vigencia	= clock_timestamp()
		where	nr_sequencia	= nr_seq_regra_w;
		end;
	end loop;
	close C01;
elsif (ie_acao_p = 'E') then
	update	regra_acao_pag_duplic
	set	dt_fim_vigencia	= clock_timestamp()
	where	nr_sequencia	= nr_seq_regra_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
