SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_repasse_prot_conta (cd_estabelecimento_p bigint, nr_seq_protocolo_p bigint, nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_interno_conta_w	bigint;
nr_seq_procedimento_w	bigint;
nr_seq_proc_repasse_w	bigint;
nr_seq_material_w	bigint;
nr_seq_mat_repasse_w	bigint;
cd_regra_w		bigint;
nr_seq_terceiro_w	bigint;
ie_pagto_w		varchar(10);
qt_pagto_w		bigint;
qt_pagto_terc_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_interno_conta
from	conta_paciente a
where	a.nr_seq_protocolo		= nr_seq_protocolo_p
and	coalesce(nr_seq_protocolo_p,0)	> 0
and	coalesce(nr_interno_conta_p,0)	= 0

union

select	a.nr_interno_conta
from	conta_paciente a
where	a.nr_interno_conta		= nr_interno_conta_p
and	coalesce(nr_interno_conta_p,0)	> 0
and	coalesce(nr_seq_protocolo_p,0)	= 0;

c02 CURSOR FOR
SELECT	a.nr_sequencia
from	procedimento_paciente a
where	a.nr_interno_conta	= nr_interno_conta_w
and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

c03 CURSOR FOR
SELECT	a.cd_regra,
	a.nr_seq_terceiro,
	a.nr_sequencia
from	procedimento_repasse a
where	a.nr_seq_procedimento	= nr_seq_procedimento_w;

c04 CURSOR FOR
SELECT	a.nr_sequencia
from	material_atend_paciente a
where	a.nr_interno_conta	= nr_interno_conta_w
and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

c05 CURSOR FOR
SELECT	a.cd_regra,
	a.nr_seq_terceiro,
	a.nr_sequencia
from	material_repasse a
where	a.nr_seq_material	= nr_seq_material_w;


BEGIN

select	count(*)
into STRICT	qt_pagto_w
from	regra_repasse_terceiro a
where	a.ie_pagto		= 'G'
and	a.cd_estabelecimento	= cd_estabelecimento_p;

select	count(*)
into STRICT	qt_pagto_terc_w
from	terceiro a
where	a.ie_forma_pagto	= 'G';

if (qt_pagto_w > 0) or (qt_pagto_terc_w > 0) then

	open C01;
	loop
	fetch C01 into
		nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		open C02;
		loop
		fetch C02 into
			nr_seq_procedimento_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			open C03;
			loop
			fetch C03 into
				cd_regra_w,
				nr_seq_terceiro_w,
				nr_seq_proc_repasse_w;
			EXIT WHEN NOT FOUND; /* apply on C03 */
				begin

				begin
				select	a.ie_pagto
				into STRICT	ie_pagto_w
				from	regra_repasse_terceiro a
				where	a.cd_regra	= cd_regra_w  LIMIT 1;
				exception
				when others then
					ie_pagto_w	:= null;
				end;

				if (ie_pagto_w = 'T') then
					select	max(a.ie_forma_pagto)
					into STRICT	ie_pagto_w
					from	terceiro a
					where	a.nr_sequencia	= nr_seq_terceiro_w;
				end if;

				if (ie_pagto_w	= 'G') then

					update	procedimento_repasse
					set	vl_liberado		= vl_repasse,
						dt_liberacao		= clock_timestamp(),
						nm_usuario_lib		= nm_usuario_p,
						ie_status		= 'S'
					where	nr_sequencia		= nr_seq_proc_repasse_w;

				end if;

				end;
			end loop;
			close C03;

			end;
		end loop;
		close C02;

		open C04;
		loop
		fetch C04 into
			nr_seq_material_w;
		EXIT WHEN NOT FOUND; /* apply on C04 */
			begin

			open C05;
			loop
			fetch C05 into
				cd_regra_w,
				nr_seq_terceiro_w,
				nr_seq_mat_repasse_w;
			EXIT WHEN NOT FOUND; /* apply on C05 */
				begin

				begin
				select	a.ie_pagto
				into STRICT	ie_pagto_w
				from	regra_repasse_terceiro a
				where	a.cd_regra	= cd_regra_w  LIMIT 1;
				exception
				when others then
					ie_pagto_w	:= null;
				end;

				if (ie_pagto_w = 'T') then
					select	max(a.ie_forma_pagto)
					into STRICT	ie_pagto_w
					from	terceiro a
					where	a.nr_sequencia	= nr_seq_terceiro_w;
				end if;

				if (ie_pagto_w	= 'G') then
					update	material_repasse
					set	vl_liberado	= vl_repasse,
						dt_liberacao	= clock_timestamp(),
						nm_usuario_lib	= nm_usuario_p,
						ie_status	= 'S'
					where	nr_sequencia	= nr_seq_mat_repasse_w;
				end if;

				end;
			end loop;
			close C05;

			end;
		end loop;
		close C04;

		end;
	end loop;
	close C01;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE liberar_repasse_prot_conta (cd_estabelecimento_p bigint, nr_seq_protocolo_p bigint, nr_interno_conta_p bigint, nm_usuario_p text) FROM PUBLIC;

