SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_repasse_terceiro ( nr_repasse_terceiro_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_terceiro_w	bigint;
vl_vencimento_w		double precision;
vl_liquido_w		double precision;
cd_estabelecimento_w	smallint;
nr_sequencia_regra_w	bigint;
nm_usuario_lib_w	varchar(15);
cd_perfil_lib_w		integer;
count_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	regra_aprovacao_repasse a
	where	coalesce(coalesce(a.nr_seq_terceiro,nr_seq_terceiro_w),0)	= coalesce(nr_seq_terceiro_w,0)
	and	vl_vencimento_w between coalesce(a.vl_minimo,0) and coalesce(a.vl_maximo,coalesce(vl_vencimento_w,0))
	and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and fim_dia(coalesce(dt_fim_vigencia,clock_timestamp()));

C02 CURSOR FOR
	SELECT	a.nm_usuario_lib,
		a.cd_perfil_lib
	from	regra_aprovacao_repas_usu a
	where	a.nr_seq_regra = nr_sequencia_regra_w
	and     (a.nm_usuario_lib IS NOT NULL AND a.nm_usuario_lib::text <> '');


BEGIN

if (ie_acao_p = 'I') then

	select	count(*)
	into STRICT	count_w
	from	repasse_terceiro_aprovacao
	where   nr_repasse_terceiro = nr_repasse_terceiro_p;

	if (count_w > 0) then
		-- Este repasse já foi liberado. Favor verificar a liberação na pasta Usuários aprovação.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(261353);
	end if;

	select	max(a.nr_seq_terceiro),
		max(b.vl_vencimento),
		max(b.vl_liquido),
		max(a.cd_estabelecimento)
	into STRICT	nr_seq_terceiro_w,
		vl_vencimento_w,
		vl_liquido_w,
		cd_estabelecimento_w
	from	repasse_terceiro a,
		repasse_terceiro_venc b
	where   b.nr_repasse_terceiro	= a.nr_repasse_terceiro
	and	a.nr_repasse_terceiro 	= nr_repasse_terceiro_p;

	open C01;
	loop
	fetch C01 into
		nr_sequencia_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		open C02;
		loop
		fetch C02 into
			nm_usuario_lib_w,
			cd_perfil_lib_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			insert into repasse_terceiro_aprovacao(dt_aprovacao,
								dt_atualizacao,
								dt_atualizacao_nrec,
								nm_usuario,
								nm_usuario_aprovacao,
								nm_usuario_nrec,
								nr_repasse_terceiro ,
								nr_sequencia)
							values (null,
								clock_timestamp(),
								clock_timestamp(),
								nm_usuario_p,
								nm_usuario_lib_w,
								nm_usuario_p,
								nr_repasse_terceiro_p,
								nextval('repasse_terceiro_aprovacao_seq'));
			end;
		end loop;
		close C02;

		end;
	end loop;
	close C01;

	commit;

elsif (ie_acao_p = 'E') then

	select	count(*)
	into STRICT	count_w
	from	repasse_terceiro_aprovacao
	where	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '')
	and     nr_repasse_terceiro = nr_repasse_terceiro_p;

	if (count_w > 0) then
		-- Reste repasse ja possui aprovação de um ou mais usuários.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(261358);
	else
		delete from repasse_terceiro_aprovacao
		where  nr_repasse_terceiro = nr_repasse_terceiro_p;

		commit;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
