CREATE OR REPLACE PROCEDURE liberar_repasse_valor_fixo ( nr_seq_proc_repasse_p bigint, nr_seq_mat_repasse_p bigint, nm_usuario_p text ) AS $body$
BEGIN

if (coalesce(nr_seq_proc_repasse_p, 0) > 0) then

	update	procedimento_repasse
	set	dt_atualizacao	= clock_timestamp(),
		dt_liberacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		nm_usuario_lib	= nm_usuario_p,
		vl_liberado	= vl_repasse,
		ie_status		= 'R'
	where	nr_sequencia	= nr_seq_proc_repasse_p;

elsif (coalesce(nr_seq_mat_repasse_p, 0) > 0) then

	update	material_repasse
	set	dt_atualizacao	= clock_timestamp(),
		dt_liberacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		nm_usuario_lib	= nm_usuario_p,
		vl_liberado	= vl_repasse,
		ie_status		= 'R'
	where	nr_sequencia	= nr_seq_mat_repasse_p;

end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
