CREATE OR REPLACE PROCEDURE liberar_requisito_usuario (NR_SEQUENCIA_P REQUISITO_USUARIO_PERGUNTA.NR_SEQUENCIA%type) AS $body$
DECLARE


DS_PERGUNTA_w REQUISITO_USUARIO_PERGUNTA.DS_PERGUNTA%type;
DS_PERGUNTA_ING_w REQUISITO_USUARIO_PERGUNTA.DS_PERGUNTA_ING%type;
DS_EXPLICACAO_w REQUISITO_USUARIO_PERGUNTA.DS_EXPLICACAO%type;
DS_EXPLICACAO_ING_w REQUISITO_USUARIO_PERGUNTA.DS_EXPLICACAO_ING%type;

BEGIN

  if (NR_SEQUENCIA_P IS NOT NULL AND NR_SEQUENCIA_P::text <> '') then
    select 
          DS_PERGUNTA,
          DS_PERGUNTA_ING,
          DS_EXPLICACAO,
          DS_EXPLICACAO_ING
    into STRICT 
          DS_PERGUNTA_W,
          DS_PERGUNTA_ING_w,
          DS_EXPLICACAO_W,
          DS_EXPLICACAO_ING_W
    from  REQUISITO_USUARIO_PERGUNTA 
    where NR_SEQUENCIA = NR_SEQUENCIA_P
    and   coalesce(DT_LIBERACAO::text, '') = '';

    if (DS_PERGUNTA_W IS NOT NULL AND DS_PERGUNTA_W::text <> '' AND DS_PERGUNTA_ING_W IS NOT NULL AND DS_PERGUNTA_ING_W::text <> '') and (DS_EXPLICACAO_W IS NOT NULL AND DS_EXPLICACAO_W::text <> '' AND DS_EXPLICACAO_ING_W IS NOT NULL AND DS_EXPLICACAO_ING_W::text <> '') then
      update REQUISITO_USUARIO_PERGUNTA
      set DT_ATUALIZACAO = clock_timestamp(),
        NM_USUARIO = wheb_usuario_pck.get_nm_usuario,
        DT_LIBERACAO = clock_timestamp(),
        IE_STATUS = 1
      where 
        NR_SEQUENCIA = NR_SEQUENCIA_P;
      commit;
    else
      CALL Wheb_mensagem_pck.exibir_mensagem_abort(1098062);
    end if;	
  end if;
end;
$body$
